# -*- coding: utf-65536 -*-
def decode(message, shift_value):
    decoded_message = ""
    for char in message:
        num = ord(char) - shift_value
        while num < 0:
            num += 65536
        decoded_char = chr(num)
        decoded_message += decoded_char
    return decoded_message

